changeSet.sql:
CREATE TABLE IF NOT EXISTS maintenance ( id INTEGER auto_increment primary key, discription VARCHAR(200) );
CREATE TABLE IF NOT EXISTS service_center ( id INTEGER auto_increment primary key, name VARCHAR(100), country_name VARCHAR(100), city_name VARCHAR(100), address VARCHAR(200),
);
CREATE TABLE IF NOT EXISTS service_center_maintenance (
id INTEGER AUTO_INCREMENT PRIMARY KEY, service_center_id INTEGER NOT NULL, maintenance_id INTEGER NOT NULL ,
FOREIGN KEY (maintenance_id) REFERENCES maintenance, FOREIGN KEY (service_center_id) REFERENCES service_center
);
